            self.emit('ignored', message);
            return;
        }
        if (message.sequence != self.book.sequence + 1) {
            self.changeState(self.STATES.error);
        }
        self.book.sequence = message.sequence;

        self.emit(message.type, new Models.Timestamped(message, t));
    };
}

