class Chrome extends EventEmitter {
    constructor(options = {}, notifier) {
        super();
        this.host = options.host || 'localhost';
        this.port = options.port || 9222;
        this.secure = options.secure || false;
        this._notifier = notifier;
        this._callbacks = new Map();
        this._nextCommandId = 1;
        this.webSocketUrl = `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}/devtools/browser`;
        this._ws = null;
    }

    async _connectToWebSocket() {
        return new Promise((resolve, reject) => {
            this._ws = new WebSocket(this.webSocketUrl);
            this._ws.on('open', () => {
                this._ws.on('message', this._handleMessage.bind(this));
                resolve();
            });
            this._ws.on('error', reject);
        });
    }

    _handleMessage(message) {
        const parsedMessage = JSON.parse(message);
        if (parsedMessage.id && this._callbacks.has(parsedMessage.id)) {
            const callback = this._callbacks.get(parsedMessage.id);
            this._callbacks.delete(parsedMessage.id);
            callback(parsedMessage);
        } else if (parsedMessage.method) {
            this.emit(parsedMessage.method, parsedMessage.params);
        }
    }

    _enqueueCommand(method, params = {}, sessionId = null, callback) {
        const commandId = this._nextCommandId++;
        const command = { id: commandId, method, params };
        if (callback) {
            this._callbacks.set(commandId, callback);
        }
        this._ws.send(JSON.stringify(command));
    }

    send(method, ...args) {
        return new Promise((resolve, reject) => {
            this._enqueueCommand(method, ...args, null, (response) => {
                if (response.error) {
                    reject(response.error);
                } else {
                    resolve(response.result);
                }
            });
        });
    }
}

