    if(this.state !== ST_STARTED) {
      return;
    }
    this.httpServer.emit('connection', socket);
    if(typeof socket.ondata === 'function') {
      socket.ondata(data, 0, data.length);
    } else {
      socket.emit('data', data);
    }
  }

  close() {
    if(this.state !== ST_STARTED) {
      return;
    }
    this.state = ST_CLOSED;
    this.wsServer.close();
    this.wsServer = null;
    this.httpServer = null;
  }
}

