function manageTestExecution() {
    const socket = new WebSocket('ws://test-server-url');
    const maxParallelRuns = 5;
    const subscriptionKey = Date.now();
    const queue = [];
    let numRunning = 0;

    function processQueue() {
        while (numRunning < maxParallelRuns && queue.length > 0) {
            const test = queue.shift();
            numRunning++;
            const testWindow = window.open(test.url, '_blank');
            const testId = test.id;

            testWindow.onload = function() {
                testWindow.postMessage({ key: subscriptionKey, testId: testId }, '*');
            };
        }
    }

    socket.onopen = function() {
        console.log('WebSocket connection established');
        socket.send(JSON.stringify({ action: 'subscribe', key: subscriptionKey }));
    };

    socket.onmessage = function(event) {
        const message = JSON.parse(event.data);
        if (message.key === subscriptionKey) {
            if (message.type === 'result') {
                document.getElementById('results').innerHTML += `<div>${message.testId}: ${message.result}</div>`;
                numRunning--;
                processQueue();
            } else if (message.type === 'status') {
                document.getElementById('status').innerHTML += `<div>${message.testId}: ${message.status}</div>`;
            }
        }
    };

    window.addTestToQueue = function(test) {
        queue.push(test);
        processQueue();
    };
}

