        this._socket.onmessage = (event) => this._onmessage(event.data);
        this._socket.onerror = (error) => this._onerror(error);
        this._socket.onclose = () => this._reconnect(address, encryption);
    }

    setAddress(address, encryption) {
        this._socket.close();
        this._socket = new WebSocket((encryption ? 'wss://' : 'ws://') + address);
    }

    addListener(method, callback) {
        this._listeners[method] = callback;
    }

    removeListener(method) {
        delete this._listeners[method];
    }

    request(method, params, id, successCallback, errorCallback) {
        const requestData = this._formatData(method, params, id);
        this._handles[id] = { success: successCallback, error: errorCallback };
        this._send(requestData);
    }

    batchRequest(requests, successCallback, errorCallback) {
        requests.forEach((req) => {
            const id = req.id || Math.random().toString(36).substring(2);
            this.request(req.method, req.params, id, successCallback, errorCallback);
        });
    }

    _formatData(method, params, id) {
        return JSON.stringify({
            jsonrpc: '2.0',
            method: this._namespace + method,
            params: params,
            id: id
        });
    }

    _send(data) {
        if (this._socket.readyState === WebSocket.OPEN) {
            this._socket.send(data);
        } else {
            this._socket.addEventListener('open', () => this._socket.send(data));
        }
    }

    _onerror(error) {
        Object.values(this._handles).forEach(handle => handle.error && handle.error(error));
        this._handles = {};
    }

    _onmessage(message) {
        const response = JSON.parse(message);
        if (response.id && this._handles[response.id]) {
            const handle = this._handles[response.id];
            if (response.error) {
                handle.error && handle.error(response.error);
            } else {
                handle.success && handle.success(response.result);
            }
            delete this._handles[response.id];
        } else if (response.method && this._listeners[response.method]) {
            this._listeners[response.method](response.params);
        }
    }

    _reconnect(address, encryption) {
        setTimeout(() => {
            this.setAddress(address, encryption);
        }, 1000);
    }
