        this.ws = new WebSocket('wss://your-websocket-url');

        this.ws.onopen = () => {
            this.wsActive = true;
            this.snack.notify('WebSocket connection established.');
        };

        this.ws.onmessage = (event) => {
            const msg = JSON.parse(event.data);
            callback(msg);
        };

        this.ws.onerror = (error) => {
            this.snack.notify('WebSocket error: ' + error.message);
        };

        this.ws.onclose = (event) => {
            this.wsActive = false;
            this.snack.notify('WebSocket connection closed: ' + event.reason);
            if (event.code !== 1000) {
                setTimeout(() => this.listen(callback), 1000); // Reconnect on error
            }
        };
    }

    close() {
        if (this.ws) {
            this.ws.close(1000, 'Normal closure');
            this.ws = null;
        }
    }
}

