function startRecording(data) {
    const { host, port, language, task, modelSize, useVad } = data;
    const ws = new WebSocket(`ws://${host}:${port}`);
    let isServerReady = false;
    let isCapturing = false;
    let mediaRecorder;
    let audioChunks = [];

    ws.onopen = () => {
        console.log('WebSocket connection established');
        ws.send(JSON.stringify({ action: 'start', language, task, modelSize, useVad }));
    };

    ws.onmessage = (event) => {
        const response = JSON.parse(event.data);
        if (response.type === 'status') {
            console.log('Server status:', response.message);
            isServerReady = response.ready;
        } else if (response.type === 'transcription') {
            console.log('Transcription result:', response.transcription);
            browser.runtime.sendMessage({ transcription: response.transcription });
        }
    };

    ws.onerror = (error) => {
        console.error('WebSocket error:', error);
    };

    ws.onclose = () => {
        console.log('WebSocket connection closed');
        isServerReady = false;
        isCapturing = false;
    };

    async function initAudio() {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        mediaRecorder = new MediaRecorder(stream);
        mediaRecorder.ondataavailable = (event) => {
            audioChunks.push(event.data);
            if (isServerReady && isCapturing) {
                ws.send(event.data);
            }
        };
        mediaRecorder.onstop = () => {
            const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
            audioChunks = [];
            ws.send(audioBlob);
        };
    }

    async function startCapture() {
        if (!isCapturing) {
            isCapturing = true;
            await initAudio();
            mediaRecorder.start(100); // Send audio data every 100ms
        }
    }

    async function stopCapture() {
        if (isCapturing) {
            isCapturing = false;
            mediaRecorder.stop();
        }
    }

    startCapture();

    return {
        stop: stopCapture
    };
}

