                this.setLineGoThrough(data.lineno);
            }
        };
        this.ws.onclose = () => {
            this.wsOpen = false;
        };
    }

    runPython(code) {
        return new Promise((resolve, reject) => {
            if (!this.wsOpen) {
                reject('WebSocket is not open');
                return;
            }
            this.running = true;
            this.ws.send(JSON.stringify({ type: 'execute', code }));
            this.ws.onmessage = (event) => {
                const data = JSON.parse(event.data);
                if (data.type === 'result') {
                    this.running = false;
                    resolve(data.result);
                } else if (data.type === 'error') {
                    this.running = false;
                    reject(data.error);
                }
            };
        });
    }

    restartKernel() {
        if (this.wsOpen) {
            this.restarting = true;
            this.ws.send(JSON.stringify({ type: 'restart' }));
            this.ws.onmessage = (event) => {
                const data = JSON.parse(event.data);
                if (data.type === 'restart_complete') {
                    this.restarting = false;
                }
            };
        }
    }

    stopDebugging() {
        if (this.wsOpen && this.running) {
            this.ws.send(JSON.stringify({ type: 'interrupt' }));
            this.running = false;
        }
    }

