        this.consoleData.push(text);
        this.editor.appendOutput(text);
    }

    resetConsole() {
        this.consoleData = [];
        this.editor.clearOutput();
    }

    resetEditor() {
        this.editor.clearBreakpoints();
    }

    setLineGoThrough(lineno) {
