    constructor(handlers, options = {}, callback) {
        if (!Array.isArray(handlers) || handlers.length === 0) {
            throw new Error('At least one handler must be provided');
        }
        this._server = new WebSocket.Server(options);
        this.handlers = handlers;
        this.options = options;
        this._server.on('connection', (socket, request) => this.handleSession(socket, request));
        if (callback) {
            this._server.on('listening', callback);
        }
    }

    get server() {
        return this._server;
    }

    close(cb) {
        this._server.close(cb);
    }

    async handleSession(socket, request) {
        for (const handler of this.handlers) {
            if (await handler.accept(request)) {
                handler.onConnect(socket, request);
                return;
            }
        }
        socket.close();
    }

    log(...msg) {
        if (this.options.logger) {
            this.options.logger(...msg);
        } else {
            console.log(...msg);
        }
    }
}

