function downloadIcons(url, style, callback) {
    const axios = require('axios');
    const fs = require('fs');
    const path = require('path');

    const targetImagePath = './icons';
    const iconData = [];
    const existingNames = new Set();

    if (!fs.existsSync(targetImagePath)) {
        fs.mkdirSync(targetImagePath);
    }

    axios.get(url, {
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    })
    .then(response => {
        const icons = response.data.icons; // assume icons are in response.data.icons
        icons.forEach(icon => {
            const { name, svg, category, tags, code, unicode, pro } = icon;

            if (!existingNames.has(name)) {
                existingNames.add(name);
                const svgPath = path.join(targetImagePath, `${name}.svg`);

                fs.writeFile(svgPath, svg, (err) => {
                    if (err) {
                        return callback(err);
                    }

                    iconData.push({ name, svg: svgPath, category, style, tags, code, unicode, pro });
                });
            } else {
                console.log(`Duplicate icon ${name} skipped.`);
            }
        });

        callback(null, iconData.sort((a, b) => a.name.localeCompare(b.name)));
    })
    .catch(error => {
        console.error('Error downloading icons:', error);
        callback(error);
    });
}

