class AjaxTransport {
    constructor() {
        this.xhrId = 0;
        this.xhrCallbacks = {};
        this.xhrSuccessStatus = { 200: true, 201: true, 204: true };
        this.xhrSupported = this.createXHR();
    }

    createXHR() {
        try {
            return new XMLHttpRequest();
        } catch (e) {
            return false;
        }
    }

    sendRequest(options, headers, complete) {
        if (!this.xhrSupported) return;

        const xhr = this.createXHR();
        if (!xhr) return;

        const id = this.xhrId++;
        this.xhrCallbacks[id] = complete;

        xhr.open(options.type || 'GET', options.url, options.async !== false);

        for (const header in headers) {
            if (headers.hasOwnProperty(header)) {
                xhr.setRequestHeader(header, headers[header]);
            }
        }

        xhr.onreadystatechange = () => {
            if (xhr.readyState === 4) {
                const status = xhr.status;
                const callback = this.xhrCallbacks[id];
                delete this.xhrCallbacks[id];

                if (xhrSuccessStatus[status]) {
                    callback(null, xhr.responseText);
                } else {
                    callback(new Error(`Request failed with status ${status}`));
                }
            }
        };

        xhr.send(options.data || null);
    }

    abortRequest(callback) {
        for (const id in this.xhrCallbacks) {
            if (this.xhrCallbacks.hasOwnProperty(id)) {
                const xhr = this.xhrCallbacks[id].xhr;
                if (xhr) {
                    xhr.abort();
                    delete this.xhrCallbacks[id];
                    if (callback) callback();
                }
            }
        }
    }

    getTransport(options) {
        if (this.xhrSupported) {
            return {
                send: (headers, complete) => this.sendRequest(options, headers, complete),
                abort: (callback) => this.abortRequest(callback)
            };
        }
        return undefined;
    }
}

