function fetchRequest(e, n = {}) {
    return new Promise((resolve, reject) => {
        const xhr = new XMLHttpRequest();
        const method = n.method || 'get';
        xhr.open(method, e);

        if (n.credentials) {
            xhr.withCredentials = n.credentials === 'include';
        }

        if (n.headers) {
            Object.keys(n.headers).forEach(key => {
                xhr.setRequestHeader(key, n.headers[key]);
            });
        }

        xhr.onload = () => {
            const response = {
                ok: xhr.status >= 200 && xhr.status < 300,
                statusText: xhr.statusText,
                status: xhr.status,
                url: xhr.responseURL,
                headers: {
                    keys: () => xhr.getAllResponseHeaders().trim().split(/[\r\n]+/).map(line => line.split(':')[0]),
                    entries: () => xhr.getAllResponseHeaders().trim().split(/[\r\n]+/).map(line => line.split(':', 2)),
                    get: (name) => {
                        const headers = xhr.getAllResponseHeaders().trim().split(/[\r\n]+/);
                        for (let header of headers) {
                            const [key, value] = header.split(':', 2);
                            if (key.toLowerCase() === name.toLowerCase()) {
                                return value.trim();
                            }
                        }
                        return null;
                    },
                    has: (name) => {
                        return xhr.getAllResponseHeaders().toLowerCase().includes(name.toLowerCase() + ':');
                    }
                },
                text: () => Promise.resolve(xhr.responseText),
                json: () => Promise.resolve(JSON.parse(xhr.responseText)),
                blob: () => Promise.resolve(new Blob([xhr.response])),
                clone: () => response
            };
            resolve(response);
        };

        xhr.onerror = () => reject(new Error('Network error'));

        if (n.body) {
            xhr.send(n.body);
        } else {
            xhr.send();
        }
    });
}

