class TwitterHTTPClient {
    constructor(config) {
        this.config = config;
        this._twitter_time_minus_local_time_ms = 0;
    }

    request(method, path, params, callback) {
        return new Promise((resolve, reject) => {
            const reqOpts = {
                method: method,
                url: `https://api.twitter.com/1.1${path}`,
                headers: {
                    'Authorization': `Bearer ${this.config.bearerToken}`,
                    'Content-Type': 'application/json'
                },
                json: true,
                body: params
            };
            this._doRestApiRequest(reqOpts, {}, method, (err, resp) => {
                if (err) {
                    reject(err);
                } else {
                    this._updateClockOffsetFromResponse(resp);
                    resolve(resp);
                }
            });
        });
    }

    _doRestApiRequest(reqOpts, twitOptions, method, callback) {
        const request = require('request');
        request(reqOpts, (error, response, body) => {
            if (error) {
                callback(error);
            } else {
                callback(null, body);
            }
        });
    }

    _updateClockOffsetFromResponse(resp) {
        const serverTime = new Date(resp.headers.date).getTime();
        const localTime = Date.now();
        this._twitter_time_minus_local_time_ms = serverTime - localTime;
    }
}

