const express = require('express');
const bodyParser = require('body-parser');
const connectRest = require('connect-rest');
const mongoose = require('mongoose');
const domain = require('domain');
const cluster = require('cluster');
const Attraction = require('./models/Attraction');

const startServer = () => {
    const app = express();
    const PORT = process.env.PORT || 3000;

    app.use(bodyParser.json());

    const options = {
        context: 'rest',
        domain: true,
        onError: (err, req, res) => {
            console.error(err);
            res.status(500).send({ error: 'Internal Server Error' });
        }
    };

    const rest = connectRest(options);
    app.use(rest.process);

    app.get('/attractions', async (req, res) => {
        try {
            const attractions = await Attraction.find({ approved: true });
            res.json(attractions);
        } catch (err) {
            res.status(500).send({ error: 'Failed to retrieve attractions' });
        }
    });

    app.post('/attraction', async (req, res) => {
        const { name, description, location, email } = req.body;
        try {
            const newAttraction = new Attraction({ name, description, location, email });
            await newAttraction.save();
            res.status(201).send({ id: newAttraction._id });
        } catch (err) {
            res.status(400).send({ error: 'Failed to create attraction' });
        }
    });

    app.get('/attraction/:id', async (req, res) => {
        try {
            const attraction = await Attraction.findById(req.params.id);
            if (!attraction) {
                return res.status(404).send({ error: 'Attraction not found' });
            }
            res.json(attraction);
        } catch (err) {
            res.status(500).send({ error: 'Failed to retrieve attraction' });
        }
    });

    const server = app.listen(PORT, () => {
        console.log(`Server is running on port ${PORT}`);
    });

    const d = domain.create();
    d.on('error', (err) => {
        console.error('Domain error:', err);
        server.close(() => {
            process.exit(1);
        });
    });

    d.run(() => {
        mongoose.connect(process.env.MONGODB_URI, { useNewUrlParser: true, useUnifiedTopology: true })
            .then(() => console.log('Connected to MongoDB'))
            .catch(err => console.error('MongoDB connection error:', err));
    });

    return server;
};

