    const isReferenceMode = port === 8000;

    app.engine('handlebars', exphbs());
    app.set('view engine', 'handlebars');
    app.set('views', isReferenceMode ? path.join(__dirname, 'views/reference') : path.join(__dirname, 'views/development'));

    app.use(express.static(path.join(__dirname, 'public')));

    app.get('/test/:component', (req, res) => {
        const component = req.params.component;
        res.render(component, { mode: isReferenceMode ? 'reference' : 'development' });
    });

    process.title = isReferenceMode ? 'Regression Test Server - Reference Mode' : 'Regression Test Server - Development Mode';

    app.listen(port, () => {
        console.log(`Server is running on port ${port}`);
    });

    return app;
}

