      if (!email) {
        return res.status(400).send('Email is required');
      }
      res.send(`Subscribed to newsletter with email: ${email}`);
    });
  }

  startServer() {
    this.app.listen(this.port, () => {
      console.log(`Server listening on port ${this.port}`);
    });
