const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

function setupExpressServer() {
  const app = express();
  const imagePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

  app.use(responseTime());

  app.get('/non-stream-sync', (req, res) => {
    try {
      const data = fs.readFileSync(imagePath);
      res.send(data);
    } catch (error) {
      console.error(error);
      res.status(500).send('Internal Server Error');
    }
  });

  app.get('/non-stream-async', (req, res) => {
    fs.readFile(imagePath, (err, data) => {
      if (err) {
        console.error(err);
        return res.status(500).send('Internal Server Error');
      }
      res.send(data);
    });
  });


  app.get('/stream-createReadStream', (req, res) => {
    const stream = fs.createReadStream(imagePath);
    stream.on('error', (error) => {
      console.error(error);
      res.status(500).send('Internal Server Error');
    });
    stream.pipe(res);
  });

  app.get('/stream-createReadStream-pipe', (req, res) => {
    fs.createReadStream(imagePath).pipe(res);
  });

  app.listen(3000, () => {
    console.log('Server listening on port 3000');
