const express = require('express');
const rateLimit = require('express-rate-limit');
const cors = require('cors');
const puppeteer = require('puppeteer');
const fs = require('node:fs/promises');
const path = require('node:path');

async function setupExpressServer() {
  const app = express();
  const port = process.env.PORT || 3000;

  const limiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    max: 100,
    standardHeaders: true,
    legacyHeaders: false,
  });

  app.use(limiter);
  app.use(cors());
  app.use(express.json());

  app.get('/query', async (req, res) => {
    try {
      const url = req.query.url;
      const selectors = JSON.parse(req.query.selectors);
      const browser = await puppeteer.launch();
      const page = await browser.newPage();
      await page.goto(url);
      const results = {};
      for (const selector of selectors) {
        const elements = await page.$$(selector);
        results[selector] = await Promise.all(
          elements.map(async (element) => ({
            innerText: await element.getProperty('innerText').then((p) => p.jsonValue()),
            href: await element.getProperty('href').then((p) => p.jsonValue()),
          }))
        );
      }
      await browser.close();
      res.json(results);
    } catch (error) {
      console.error(error);
      res.status(500).json({ error: 'An error occurred' });
    }
  });


  app.get('/fullHtml', async (req, res) => {
    try {
      const url = req.query.url;
      const browser = await puppeteer.launch();
      const page = await browser.newPage();
      await page.goto(url);
      const html = await page.content();
      await browser.close();
      res.send(html);
    } catch (error) {
      console.error(error);
      res.status(500).json({ error: 'An error occurred' });
    }
  });

  const indexFile = path.join(__dirname, 'index.html');
  try {
    await fs.access(indexFile);
    app.get('/', (req, res) => {
      res.sendFile(indexFile);
    });
  } catch (error) {
    console.warn('index.html not found, serving default message.');
    app.get('/', (req, res) => {
      res.send('Service is running. Use /query or /fullHtml endpoints.');
    });
  }


  app.listen(port, () => {
    console.log(`Server listening on port ${port}`);
  });
}

setupExpressServer();

