const cors = require('cors');
const wiki = require('wikipedia');

const setupExpressServer = () => {
  const app = express();
  const port = 3001;

  const corsOptions = {
    origin: 'http://localhost:3000',
    methods: 'GET',
    allowedHeaders: 'Content-Type',
  };

  app.use(cors(corsOptions));

  app.get('/wikipedia_summaries', async (req, res) => {
    try {
      const keyword = req.query.keyword;
      const links = await wiki.search(keyword);
      const summariesAndCategories = [];

      for (const link of links.results) {
        try {
          const page = await wiki.page(link.title);
          const summary = await page.summary();
          const categories = await page.categories();
          summariesAndCategories.push({ title: link.title, summary, categories });
        } catch (error) {
          console.error(`Error fetching details for ${link.title}:`, error);
        }
      }

      res.json(summariesAndCategories);
    } catch (error) {
      console.error('Error fetching Wikipedia links:', error);
      res.status(500).json({ error: 'Failed to fetch Wikipedia summaries' });
    }
