const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = config;
    this._http = null;
    this.sockets = [];
    this.path = config.path;
    this.updateServer();
  }

  updateServer() {
    this._http = http.createServer((req, res) => {
      const reqPath = decodeURIComponent(req.url);
      const filePath = path.join(this.path, reqPath);

      const sendFile = (filePath) => {
        send(req, filePath, { root: this.path })
          .on('error', (err) => {
            if (err.status === 404) {
              res.statusCode = 404;
              res.end('Not Found');
            } else {
              res.statusCode = 500;
              res.end('Internal Server Error');
            }
          })
          .pipe(res);
      };

      fs.stat(filePath, (err, stat) => {
        if (err) {
          if (err.code === 'ENOENT') {
            res.statusCode = 404;
            res.end('Not Found');
          } else {
            res.statusCode = 500;
            res.end('Internal Server Error');
          }
          return;
        }

        if (stat.isDirectory()) {
          const indexHtmlPath = path.join(filePath, 'index.html');
          fs.stat(indexHtmlPath, (err, stat) => {
            if (err && err.code === 'ENOENT') {
              const files = fs.readdirSync(filePath);
              const html = `<!DOCTYPE html><html><body><h1>Directory Listing</h1><ul>${files.map(file => `<li><a href="${encodeURIComponent(file)}">${file}</a></li>`).join('')}</ul></body></html>`;
              res.end(html);
            } else if (err) {
              res.statusCode = 500;
              res.end('Internal Server Error');
            } else {
              sendFile(indexHtmlPath);
            }
          });
        } else if (stat.isFile()) {
          if (filePath.endsWith('.md')) {
            fs.readFile(filePath, 'utf8', (err, data) => {
              if (err) {
                res.statusCode = 500;
                res.end('Internal Server Error');
                return;
              }
              const html = marked(data);
              res.end(html);
            });
          } else {
            sendFile(filePath);
          }
        }
      });
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        this.sockets.splice(this.sockets.indexOf(socket), 1);
      });
    });
  }

  start(callback) {
    this._http.listen(this.config.port, callback);
  }

  stop(callback) {
    this.sockets.forEach(socket => socket.destroy());
    this._http.close(callback);
  }
}

