      this.clients.push(jsonSocket);

      jsonSocket.on('message', message => {
        if (this.messageHandlers[message.type]) {
          this.messageHandlers[message.type](message, jsonSocket);
        }
      });

      jsonSocket.on('close', () => {
        this.clients = this.clients.filter(client => client !== jsonSocket);
      });

      jsonSocket.on('error', err => {
        console.error('Socket error:', err);
        this.clients = this.clients.filter(client => client !== jsonSocket);
      });
    });
  }

  listen(port) {
    this.tcpServer.listen(port, '0.0.0.0');
  }

  close() {
    return new Promise((resolve, reject) => {
      try {
        this.clients.forEach(client => client.end());
        this.tcpServer.close(() => resolve());
      } catch (error) {
        reject(error);
      }
    });
  }

  on(type, callback) {
    this.messageHandlers[type] = callback;
  }
}

