class TcpReader {
    constructor(transporter, opts) {
        this.transporter = transporter;
        this.opts = opts;
        this.sockets = [];
        this.logger = transporter.logger;
    }

    listen() {
        return new Promise((resolve, reject) => {
            this.server = net.createServer(socket => {
                this.onTcpClientConnected(socket);
            });

            this.server.on("error", err => {
                reject(err);
            });

            const listenOptions = process.versions.node.split(".")[0] >= 8 
                ? { port: this.opts.port, exclusive: true }
                : this.opts.port;

            this.server.listen(listenOptions, () => {
                this.opts.port = this.server.address().port;
                this.connected = true;
                resolve();
            });
        });
    }

    onTcpClientConnected(socket) {
        socket.on("error", err => {
            this.closeSocket(socket, err);
        });

        socket.on("close", () => {
            this.closeSocket(socket);
        });

        socket.setNoDelay(true);

        const parser = require("../../../../src/transporters/tcp/parser")();
        socket.pipe(parser);

        parser.on("data", (type, message) => {
            this.transporter.onIncomingMessage(type, message, socket);
        });

        parser.on("error", err => {
            this.closeSocket(socket, err);
        });

        this.sockets.push(socket);
    }

    closeSocket(socket, err) {
        if (err) {
            this.logger.warn(`TCP socket error: ${err.message}`);
        }

        const index = this.sockets.indexOf(socket);
        if (index !== -1) {
            this.sockets.splice(index, 1);
            socket.destroy();
        }
    }

    close() {
        if (this.server && this.server.listening) {
            this.server.close();
        }

        this.sockets.forEach(socket => {
            socket.destroy();
        });
        this.sockets = [];
    }
