const net = require('net');

class TcpReader {
  constructor(transporter, opts) {
    this.transporter = transporter;
    this.opts = opts;
    this.sockets = [];
    this.logger = transporter.logger;
    this.server = null;
    this.connected = false;
  }

  listen() {
    return new Promise((resolve, reject) => {
      this.server = net.createServer((socket) => this.onTcpClientConnected(socket));
      this.server.on('error', (err) => {
        this.connected = false;
        reject(err);
      });
      this.server.listen(this.opts.port, () => {
        this.connected = true;
        resolve();
      });
    });
  }

  onTcpClientConnected(socket) {
    this.sockets.push(socket);
    socket.on('data', (data) => {
      try {
        this.transporter.parser.parse(data, (err, msg) => {
          if (err) {
            this.logger.error(err);
            return;
          }
          this.transporter.onMessage(msg, socket);
        });
      } catch (err) {
        this.logger.error(err);
        this.closeSocket(socket, err);
      }
    });

    socket.on('error', (err) => this.closeSocket(socket, err));
    socket.on('end', () => this.closeSocket(socket));
    socket.on('close', () => this.closeSocket(socket));
  }

  closeSocket(socket, err) {
    if (err) {
      this.logger.error(err);
    }
    this.sockets = this.sockets.filter((s) => s !== socket);
    if (socket.writable) {
      socket.end();
    }
    socket.destroy();
  }

  close() {
    if (this.server) {
      this.server.close();
    }
    this.sockets.forEach((socket) => this.closeSocket(socket));
    this.sockets = [];
    this.connected = false;
  }
}
