const net = require('net');

class RedisInterceptor {
  constructor(Pythagora, listenPort, targetPort, intermediateData) {
    this.Pythagora = Pythagora;
    this.listenPort = listenPort;
    this.targetPort = targetPort || 6379;
    this.intermediateData = intermediateData;
    this.listenSocket = null;
    this.targetSocket = null;
    this.mode = undefined;
  }

  init() {
    return new Promise((resolve, reject) => {
      this.listenSocket = net.createServer((connection) => {
        this.targetSocket = net.createConnection({ port: this.targetPort }, () => {
          connection.on('data', (data) => {
            if (this.mode === MODES.CAPTURE) {
              this.forwardData(connection, data, true);
            } else if (this.mode === MODES.TEST) {
              const mockResponse = this.intermediateData.shift();
              if (mockResponse) {
                connection.write(mockResponse);
              }
            } else {
              this.forwardData(connection, data, false);
            }
          });

          connection.on('end', () => {
            this.targetSocket.end();
          });

          connection.on('error', (err) => {
            console.error('Client connection error:', err);
            this.targetSocket.end();
          });

          this.targetSocket.on('data', (data) => {
            if (this.mode === MODES.CAPTURE) {
              this.Pythagora.saveIntermediateData(data.toString());
            }
            connection.write(data);
          });

          this.targetSocket.on('end', () => {
            connection.end();
          });

          this.targetSocket.on('error', (err) => {
            console.error('Redis connection error:', err);
            connection.end();
          });
        });
      });

      this.listenSocket.listen(this.listenPort, () => {
        resolve();
      });

      this.listenSocket.on('error', (err) => {
        reject(err);
      });
