        socket.on('connect', () => {
          this.send(socket, JSON.stringify({ type: 'handshake', id: this.id_ }));
        });
        socket.on('data', (data) => this.on_data(data, socket));
        socket.on('end', () => this.remove_peer(socket));
        socket.on('error', () => {}); 
      } catch (error) {}
    }
  }


