function setupUDPServer(argv, io, debug) {
  const server = dgram.createSocket('udp4');
  const streams = {};

  server.on('error', (err) => {
    debug(`UDP server error:\n${err.stack}`);
    server.close();
  });

  server.on('message', (msg, rinfo) => {
    try {
      const message = JSON.parse(msg);
      const streamId = message.streamId;

      if (!streams[streamId]) {
        streams[streamId] = [];
        io.emit('stream-added', { streamId });
      }

      const stream = streams[streamId];
      stream.push(message);

      if (stream.length > 100) {
        stream.shift();
      }

      io.emit('message', { streamId, message });

    } catch (error) {
      debug(`Error parsing UDP message: ${error}`);
    }
  });

  server.on('listening', () => {
    const address = server.address();
    debug(`UDP server listening on ${address.address}:${address.port}`);
    io.emit('udp-listening', { port: address.port, host: address.address });
  });

  server.bind(argv.udpPort, argv.udpHost);

  io.on('connection', (socket) => {
    socket.emit('streams', Object.keys(streams));
    for (const streamId in streams) {
      socket.emit('stream-data', { streamId, data: streams[streamId] });
    }
  });
