const dgram = require('dgram');
const os = require('os');

class ServerDiscovery {
  constructor(broadcastPort) {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = 0;
    this.broadcastPort = broadcastPort;
    this.broadcastIP = this.calcBroadcastIP();
    this.discoveryListener = null;
  }

  setupDiscovery(callback) {
    this.discoveryListener = dgram.createSocket('udp4');
    this.discoveryListener.on('message', this.discoveryReceive.bind(this));
    this.discoveryListener.bind(this.broadcastPort, () => {
      this.discoveryListener.setBroadcast(true);
      if (callback) callback();
    });
  }

  discoveryBroadcast(type, message, callback) {
    const client = dgram.createSocket('udp4');
    const msg = JSON.stringify({ type, message });
    client.send(msg, this.broadcastPort, this.broadcastIP, (err) => {
      client.close();
      if (callback) callback(err);
    });
    this.lastDiscoveryBroadcast = Date.now();
  }

  discoveryReceive(msg, rinfo) {
    try {
      const data = JSON.parse(msg.toString());
      if (data.type === 'heartbeat') {
        this.nearbyServers[rinfo.address] = { ...data.message, timestamp: Date.now() };
      }
    } catch (e) {
      console.error('Invalid discovery message received:', e);
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
      this.discoveryListener = null;
    }
  }

  calcBroadcastIP() {
    const interfaces = os.networkInterfaces();
    for (const name in interfaces) {
      for (const iface of interfaces[name]) {
        if (iface.family === 'IPv4' && !iface.internal && iface.netmask) {
          const ipParts = iface.address.split('.');
          const maskParts = iface.netmask.split('.');
          let broadcastIP = '';
          for (let i = 0; i < 4; i++) {
            broadcastIP += (parseInt(ipParts[i]) | ~parseInt(maskParts[i])) & 255;
            if (i < 3) broadcastIP += '.';
          }
          return broadcastIP;
        }
      }
    }
    return '255.255.255.255';
  }
}

