const dgram = require('dgram');
const EventEmitter = require('events');

class MasterIPC extends EventEmitter {
  constructor(options) {
    super();
    this.options = options;
    this._sock = null;
    this.children = [];
    this.sock = {
      send: (msg, port, address, cb) => {
        const buf = Buffer.from(msg);
        this.IPCSocket().send(buf, port, address, cb);
      }
    };
  }

  IPCSocket() {
    if (!this._sock) {
      this._sock = dgram.createSocket('udp4');
      this._sock.on('message', (msg, rinfo) => {
        let data;
        try {
          data = JSON.parse(msg.toString());
        } catch (err) {
          return;
        }

        if (data.id && data.method) {
          this.invoke(data.method, data.args, this.children.find(w => w.id === data.id));
        } else if (data.event === 'master_killed') {
          this.masterKilled(data.data);
        }
      });
    }
    return this._sock;
  }

  setupIPC() {
    this.IPCSocket().bind(this.options.port, this.options.address, () => {
      this.emit('start');
    });
  }

  call(id, method, ...args) {
    const msg = JSON.stringify({ id, method, args });
    const worker = this.children.find(w => w.id === id);
    if (worker) {
      this.sock.send(msg, worker.port, worker.address);
    }
  }

  invoke(method, args, worker) {
    if (typeof this[method] === 'function') {
      this[method](args, worker);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.emit('worker connected', worker);
  }

  connectMaster(sig) {
    const killParent = () => process.kill(process.ppid, sig);
    if (this._sock && this._sock.listening) {
      killParent();
    } else {
      this.once('start', killParent);
    }
  }

  masterKilled(data) {
    this.emit('restart', data);
  }
}
