const WebSocket = require('ws');

class WebSocketImpl {
  constructor(globalObject, args, privateData) {
    this._globalObject = globalObject;
    this._ownerDocument = globalObject.document;
    this._urlRecord = privateData.urlRecord;
    this.url = this._urlRecord.url;
    this._ws = null;
    this._readyState = 0;
    this._requiredToFail = false;
    this.bufferedAmount = 0;
    this._sendQueue = [];

    this._ws = new WebSocket(this.url);

    this._ws.onopen = () => this._onConnectionEstablished();
    this._ws.onmessage = (event) => this._onMessageReceived(event.data, event.binary);
    this._ws.onclose = (event) => this._onConnectionClosed(event.code, event.reason);
    this._ws.onerror = (event) => {
      if (!this._requiredToFail) {
        console.error('WebSocket error:', event);
      }
    };
  }

  _onConnectionEstablished() {
    this._readyState = 1;
    this._sendQueue.forEach(data => this.send(data));
    this._sendQueue = [];
  }

  _onMessageReceived(data, isBinary) {
    const messageEvent = new MessageEvent('message', {
      data: data,
      origin: this._urlRecord.origin,
    });
    this._ownerDocument._dispatch(messageEvent);
  }

  _onConnectionClosed(code, reason) {
    this._readyState = 3;
    const closeEvent = new CloseEvent('close', {
      code: code,
      reason: reason,
      wasClean: true,
    });
    this._ownerDocument._dispatch(closeEvent);
    this._ws = null;
  }

  close(code, reason) {
    if (this._ws) {
      this._ws.close(code, reason);
    }
  }

  send(data) {
    if (this._readyState === 1) {
      if (typeof data === 'string') {
        this.bufferedAmount = this._ws.bufferedAmount;
        this._ws.send(data);
      } else if (data instanceof this._globalObject.Blob) {
        const reader = new FileReader();
        reader.onloadend = () => {
          this.bufferedAmount = this._ws.bufferedAmount;
          this._ws.send(reader.result);
        };
        reader.readAsArrayBuffer(data);
      }
    } else {
      this._sendQueue.push(data);
    }
  }

  get readyState() {
    return this._readyState;
  }
}


module.exports = WebSocketImpl;

