  constructor(url, protocols, options) {
    this.debug = options?.debug ?? false;
    this.automaticOpen = options?.automaticOpen ?? true;
    this.reconnectInterval = options?.reconnectInterval ?? 1000;
    this.maxReconnectInterval = options?.maxReconnectInterval ?? 30000;
    this.reconnectDecay = options?.reconnectDecay ?? 1.5;
    this.timeoutInterval = options?.timeoutInterval ?? 2000;
    this.url = url;
    this.reconnectAttempts = 0;
    this.readyState = WebSocket.CONNECTING;
    this.protocol = null;

    this._connect(protocols);

    if (this.automaticOpen) {
      this.open(false);
    }
  }

  _connect(protocols) {
    this.ws = new WebSocket(this.url, protocols);
    this.ws.onopen = (event) => this.onopen(event);
    this.ws.onclose = (event) => this.onclose(event);
    this.ws.onmessage = (event) => this.onmessage(event);
    this.ws.onerror = (event) => this.onerror(event);
  }


  addEventListener(type, listener) {
    this.ws.addEventListener(type, listener);
  }

  removeEventListener(type, listener) {
    this.ws.removeEventListener(type, listener);
  }

  dispatchEvent(event) {
    return this.ws.dispatchEvent(event);
  }


  open(wasReconnect) {
    if (this.ws.readyState !== WebSocket.CONNECTING && this.ws.readyState !== WebSocket.CLOSED) return;
    this.readyState = WebSocket.CONNECTING;
    if (!wasReconnect) this.reconnectAttempts = 0;
    if (this.debug) console.log(`ReconnectingWebSocket: connecting to ${this.url}`);
    this.dispatchEvent(this.createEvent('connecting'));
  }

  send(data) {
    if (this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(data);
    } else {
      if (this.debug) console.log('ReconnectingWebSocket: not connected, message not sent');
    }
  }

