function manageTests() {
  const socket = new WebSocket('ws://localhost:8080/test-server');
  const maxParallelRuns = 4;
  let subscriptionKey;
  const queue = [];
  let numRunning = 0;

  function processQueue() {
    while (numRunning < maxParallelRuns && queue.length > 0) {
      const test = queue.shift();
      const testWindow = window.open(test.url, '_blank');
      numRunning++;

      testWindow.addEventListener('beforeunload', () => {
        socket.send(JSON.stringify({
          type: 'testCompleted',
          subscriptionKey: subscriptionKey,
          testId: test.id,
        }));
      });
    }
  }

  socket.onopen = () => {
    subscriptionKey = Math.floor(Math.random() * 100000);
    socket.send(JSON.stringify({ type: 'subscribe', subscriptionKey: subscriptionKey }));

    const testLinks = document.querySelectorAll('.test-link');
    testLinks.forEach(link => {
      queue.push({ id: link.id, url: link.href });
    });

    processQueue();
  };

  socket.onmessage = event => {
    const message = JSON.parse(event.data);

    if (message.subscriptionKey === subscriptionKey) {
      if (message.type === 'testResult') {
        const testElement = document.getElementById(message.testId);
        if (testElement) {
          testElement.textContent = `Test ${message.testId}: ${message.result}`;
        }
        numRunning--;
        processQueue();
      } else if (message.type === 'testStatus') {
        const testElement = document.getElementById(message.testId);
        if (testElement) {
          testElement.textContent = `Test ${message.testId}: ${message.status}`;
        }
      }
    }
  };

  socket.onclose = () => {
    console.log('WebSocket connection closed.');
  };

  socket.onerror = error => {
    console.error('WebSocket error:', error);
  };
}

