    const url = `${this.updateUrl}/${this.gridKey}`;
    this.websocket = new WebSocket(url);

    this.websocket.onopen = () => {
      this.wxErrorCount = 0;
      this.heartbeat();
    };

    this.websocket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      console.log("Received:", message);
    };

    this.websocket.onerror = () => {
      this.wxErrorCount++;
      console.error("WebSocket error");
      this.reconnect();
    };

    this.websocket.onclose = (event) => {
      console.warn("WebSocket closed:", event);
      this.reconnect();
    };
  }

  sendMessage(data) {
    if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
      const compressedData = pako.gzip(JSON.stringify(data));
      this.websocket.send(compressedData);
    } else {
      console.error("WebSocket not open. Message not sent.");
    }
  }

  closeConnection() {
    clearInterval(this.retryTimer);
    if (this.websocket) {
      this.websocket.close();
    }
  }

  reconnect() {
    clearInterval(this.retryTimer);
    if (this.wxErrorCount < 5) {
      setTimeout(() => this.openWebSocket(), 2000 * this.wxErrorCount);
    } else {
      console.error("Max retry attempts reached. WebSocket connection aborted.");
    }
  }


  heartbeat() {
    clearInterval(this.retryTimer);
    this.retryTimer = setInterval(() => {
      if (this.websocket && this.websocket.readyState === WebSocket.OPEN) {
        this.websocket.send(JSON.stringify({ type: 'heartbeat' }));
      }
    }, 30000);
  }
}

