class RPCWebSocket {
  constructor(address, encryption, namespace) {
    this._namespace = namespace;
    this._listeners = {};
    this._handles = {};
    this.setAddress(address, encryption);
  }

  setAddress(address, encryption) {
    const protocol = encryption ? 'wss' : 'ws';
    const url = `${protocol}://${address}`;
    this._socket = new WebSocket(url);
    this._socket.onopen = () => {
      Object.keys(this._handles).forEach(id => {
        const data = this._handles[id].data;
        this._send(data);
      });
    };
    this._socket.onclose = () => setTimeout(() => this.setAddress(address, encryption), 5000);
    this._socket.onerror = error => this._onerror(error, this._handles);
    this._socket.onmessage = message => this._onmessage(message, this._handles, this._listeners);
  }


  addListener(method, callback) {
    this._listeners[method] = callback;
  }

  removeListener(method) {
    delete this._listeners[method];
  }

  request(method, params, id, successCallback, errorCallback) {
    const data = this._formatData(method, params, id);
    this._handles[id] = { resolve: successCallback, reject: errorCallback, data };
    this._send(data);
  }

  batchRequest(requests, successCallback, errorCallback) {
    const data = requests.map(({ method, params, id }) => this._formatData(method, params, id));
    const batchId = Math.random().toString(36).substring(2, 15);
    this._handles[batchId] = { resolve: successCallback, reject: errorCallback, data };
    this._send(data);
  }

  _formatData(method, params, id) {
    return {
      jsonrpc: '2.0',
      method: `${this._namespace}.${method}`,
      params: params,
      id: id
    };
  }

  _send(data) {
    if (this._socket.readyState === WebSocket.OPEN) {
      this._socket.send(JSON.stringify(data));
    }
  }

  _onerror(error, handles) {
    Object.values(handles).forEach(handle => {
      if (handle.reject) {
        handle.reject(error);
      }
    });
    this._handles = {};
  }

  _onmessage(message, handles, listeners) {
    try {
      const data = JSON.parse(message.data);
      if (Array.isArray(data)) {
        data.forEach(item => this._processMessage(item, handles, listeners));
      } else {
        this._processMessage(data, handles, listeners);
      }
    } catch (error) {
      console.error("Failed to parse message:", error);
    }
  }

  _processMessage(data, handles, listeners) {
    if (data.id) {
      const handle = handles[data.id];
      if (handle) {
        if (data.error) {
          if (handle.reject) {
            handle.reject(data.error);
          }
        } else {
          if (handle.resolve) {
            handle.resolve(data.result);
          }
        }
        delete handles[data.id];
      }
    } else if (data.method) {
      const listener = listeners[data.method];
      if (listener) {
        listener(data.params);
      }
    }
  }
}

