function connectServer(port) {
  if (wsSocket) {
    wsSocket.close();
  }

  wsSocket = new WebSocket(`ws://127.0.0.1:${port}`);

  wsSocket.onopen = () => {
    console.log('WebSocket connection established');
    GlobalEvents.emit('websocket-connection', { status: 'connected' });
  };

  wsSocket.onmessage = (event) => {
    const message = JSON.parse(event.data);

    if (message.type === 'config') {
      GlobalEvents.emit('config-update', message.data);
    } else if (message.type === 'checkResult') {
      if (message.data.status === 'failed') {
        chrome.notifications.create({
          type: 'basic',
          iconUrl: 'img/icon.png',
          title: 'Check Result',
          message: message.data.message,
        });
      }
    } else if (message.type === 'moduleStart') {
      GlobalEvents.emit('module-start', message.data);
    } else if (message.type === 'moduleEnd') {
      GlobalEvents.emit('module-end', message.data);
    } else if (message.type === 'mobileAppInfo') {
      GlobalEvents.emit('mobile-app-info', message.data);
    }
  };

  wsSocket.onclose = () => {
    console.log('WebSocket connection closed');
    GlobalEvents.emit('websocket-connection', { status: 'disconnected' });
    wsSocket = null;
  };

  wsSocket.onerror = (error) => {
    console.error('WebSocket error:', error);
    GlobalEvents.emit('websocket-connection', { status: 'error', error });
    wsSocket = null;
  };
}

