class WebSocketClient {
  static DISCONNECT_TIMEOUT = 5000;
  static RECONNECT_TIMEOUT = 5000;

  wsState = WebSocket.CLOSED;
  ws = null;
  wsURL = "";
  connectTS = 0;
  disconnectTID = null;
  reconnectTID = null;
  onmessage = null;

  constructor(wsURL, onmessage) {
    this.wsURL = wsURL;
    this.onmessage = onmessage;
  }

  onconnect() {
    if (this.ws && this.ws.readyState !== WebSocket.CLOSED) {
      return false;
    }
    this.ws = new WebSocket(this.wsURL);
    this.ws.onopen = this.onopen.bind(this);
    this.ws.onclose = this.onclose.bind(this);
    this.ws.onmessage = (event) => {
      if (this.onmessage) {
        try {
          const data = JSON.parse(event.data);
          this.onmessage(data);
        } catch (e) {
          console.error("Failed to parse WS message:", e);
        }
      }
    };
    this.wsState = WebSocket.CONNECTING;
    this.connectTS = Date.now();
    return true;
  }

  ondisconnect() {
    clearTimeout(this.reconnectTID);
    this.reconnectTID = null;
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    this.wsState = WebSocket.CLOSED;
  }

  onopen(e) {
    this.wsState = WebSocket.OPEN;
    clearTimeout(this.reconnectTID);
    this.reconnectTID = null;
  }

  onclose() {
    this.ws = null;
    this.wsState = WebSocket.CLOSED;
    if (Date.now() - this.connectTS > WebSocketClient.DISCONNECT_TIMEOUT) {
      this.reconnectTID = setTimeout(this.reconnect.bind(this), WebSocketClient.RECONNECT_TIMEOUT);
      return true;
    }
    return false;
  }

  send(data) {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      try {
        this.ws.send(JSON.stringify(data));
      } catch (e) {
        console.error("Failed to send WS message:", e);
      }
    }
  }

  reconnect() {
    this.ondisconnect();
    this.onconnect();
  }
}

