    this.reconnectInterval = options.reconnectInterval;
    this.maxReconnectInterval = options.maxReconnectInterval;
    this.reconnectDecay = options.reconnectDecay;
    this.timeoutInterval = options.timeoutInterval;
    this.maxReconnectAttempts = options.maxReconnectAttempts;
    this.readyState = WebSocket.CONNECTING;
    this.reconnectAttempts = 0;
    this._listeners = {};
    if (options.automaticOpen === true) {
        this.open(false);
    }
}

ReconnectingWebSocket.prototype.open = function (reconnecting) {
    if (this.socket) {
        this.socket.close();
    }
    this.socket = new WebSocket(this.url, this.protocols);
    this.socket.onopen = this.onopen.bind(this);
    this.socket.onclose = this.onclose.bind(this);
    this.socket.onmessage = this.onmessage.bind(this);
    this.socket.onerror = this.onerror.bind(this);
    this.readyState = WebSocket.CONNECTING;
    if (reconnecting) {
        this.dispatchEvent({ type: 'connecting' });
    }
};

ReconnectingWebSocket.prototype.send = function (data) {
    if (this.socket && this.socket.readyState === WebSocket.OPEN) {
        this.socket.send(data);
    } else {
        throw 'INVALID_STATE_ERR : Pausing to reconnect websocket';
    }
};

ReconnectingWebSocket.prototype.close = function (code, reason) {
    if (typeof code === 'undefined') {
        code = 1000;
    }
    if (this.socket) {
        this.socket.close(code, reason);
    }
};

ReconnectingWebSocket.prototype.refresh = function () {
    if (this.socket) {
        this.socket.close();
    }
};

ReconnectingWebSocket.prototype.onopen = function (event) {
    this.readyState = WebSocket.OPEN;
    this.reconnectAttempts = 0;
    this.dispatchEvent({ type: 'open' });
};

ReconnectingWebSocket.prototype.onclose = function (event) {
    this.readyState = WebSocket.CLOSED;
    this.dispatchEvent({ type: 'close', code: event.code, reason: event.reason });
    if (event.wasClean) {
        return;
    }
    var timeout = this.reconnectInterval * Math.pow(this.reconnectDecay, this.reconnectAttempts);
    setTimeout(this.open.bind(this, true), timeout > this.maxReconnectInterval ? this.maxReconnectInterval : timeout);
    this.reconnectAttempts++;
};

ReconnectingWebSocket.prototype.onmessage = function (event) {
    this.dispatchEvent({ type: 'message', data: event.data });
};

ReconnectingWebSocket.prototype.onerror = function (event) {
    this.dispatchEvent({ type: 'error', message: event.message });
};

ReconnectingWebSocket.prototype.addEventListener = function (type, listener) {
    if (typeof this._listeners[type] === 'undefined') {
        this._listeners[type] = [];
    }
    this._listeners[type].push(listener);
};

ReconnectingWebSocket.prototype.removeEventListener = function (type, listener) {
    if (typeof this._listeners[type] !== 'undefined') {
        var listenerArray = this._listeners[type];
        for (var i = 0; i < listenerArray.length; i++) {
            if (listenerArray[i] === listener) {
                listenerArray.splice(i, 1);
                break;
            }
        }
    }
};

ReconnectingWebSocket.prototype.dispatchEvent = function (event) {
    var listenerArray = this._listeners[event.type];
    if (listenerArray) {
        for (var i = 0; i < listenerArray.length; i++) {
            listenerArray[i](event);
        }
    }
};

if (typeof module !== 'undefined' && module.exports) {
    module.exports = ReconnectingWebSocket;
}

