    this.connectPromise = null;
    this.subscriptions = new Map();
    this.socket = null;
    this.state = 'INITIALIZED';
    this.reconnectHandle = null;
    this.emitter = new EventEmitter();
    this.on = this.emitter.on.bind(this.emitter);
    this.emit = this.emitter.emit.bind(this.emitter);
  }

  open() {
    if (this.state === 'CONNECTING' || this.state === 'CONNECTED') {
      return;
    }
    this.state = 'CONNECTING';
    this.connectPromise = new Promise((resolve, reject) => {
      this.socket = new WebSocketController(this.serverURL);
      this.socket.onopen = this._handleWebSocketOpen.bind(this);
      this.socket.onmessage = this._handleWebSocketMessage.bind(this);
      this.socket.onclose = this._handleWebSocketClose.bind(this);
      this.socket.onerror = this._handleWebSocketError.bind(this);
      this.socket.onopenPromise.then(() => {
        resolve();
      }).catch((error) => {
        this.state = 'CLOSED';
        reject(error);
      });
    });
    return this.connectPromise;
  }


  _handleWebSocketOpen() {
    const connectionRequest = {
      op: 'connect',
      applicationId: this.applicationId,
      javascriptKey: this.javascriptKey,
      masterKey: this.masterKey,
      sessionToken: this.sessionToken,
      installationId: this.installationId
    };
    this.socket.send(JSON.stringify(connectionRequest));
  }

  _handleWebSocketMessage(event) {
    const message = JSON.parse(event.data);
    if (message.op === 'connected') {
      this.id = message.clientId;
      this.state = 'CONNECTED';
      this.attempts = 0;
      this.emit('open');
    }
  }

  _handleWebSocketClose() {
    this.state = 'CLOSED';
    this.socket = null;
    this.emit('close');
    this._handleReconnect();
  }

  _handleWebSocketError(error) {
    this.emit('error', error);
    this._handleReconnect();
  }

  _handleReconnect() {
    if (this.reconnectHandle) {
      clearTimeout(this.reconnectHandle);
    }
    this.attempts++;
    const delay = Math.min(Math.pow(2, this.attempts) * 1000, 30000); // Exponential backoff with max 30s delay
    this.reconnectHandle = setTimeout(() => {
      this.open().catch(() => { /* Ignore errors during reconnection */ });
    }, delay);
  }
}

class EventEmitter {
  constructor() {
    this.listeners = {};
  }
  on(event, listener) {
    if (!this.listeners[event]) {
      this.listeners[event] = [];
    }
    this.listeners[event].push(listener);
  }
  emit(event, ...args) {
    if (this.listeners[event]) {
      this.listeners[event].forEach(listener => listener(...args));
    }
  }
}

class WebSocketController {
  constructor(url) {
    this.ws = new WebSocket(url);
    this.onopen = null;
    this.onmessage = null;
    this.onclose = null;
    this.onerror = null;
    this.onopenPromise = new Promise((resolve, reject) => {
      this.ws.onopen = () => {
        if (this.onopen) this.onopen();
        resolve();
      };
      this.ws.onerror = (error) => {
        if (this.onerror) this.onerror(error);
        reject(error);
      };
    });
    this.ws.onmessage = (event) => {
      if (this.onmessage) this.onmessage(event);
    };
    this.ws.onclose = () => {
      if (this.onclose) this.onclose();
    };
  }

  send(data) {
    this.ws.send(data);
  }

  close() {
    this.ws.close();
  }
