class WebSocketStore {
  wsActive: boolean;
  ws: WebSocket | null;

  constructor(snack, currentUser) {
    this.wsActive = false;
    this.ws = null;
    this.snack = snack;
    this.currentUser = currentUser;
  }

  listen(callback) {
    if (this.wsActive) return;

    const token = this.currentUser.getToken();
    if (!token) {
      this.snack.enqueueSnackbar('Not authenticated', { variant: 'error' });
      return;
    }

    this.ws = new WebSocket(`wss://your-websocket-server-url/?token=${token}`);
    this.wsActive = true;

    this.ws.onopen = () => {
      this.snack.enqueueSnackbar('Connected to WebSocket', { variant: 'success' });
    };

    this.ws.onmessage = (event) => {
      try {
        const msg = JSON.parse(event.data);
        callback(msg);
      } catch (error) {
        console.error('Failed to parse WebSocket message:', error);
      }
    };

    this.ws.onclose = (event) => {
      this.wsActive = false;
      this.ws = null;
      if (event.code !== 1000) {
        this.snack.enqueueSnackbar('WebSocket connection closed unexpectedly. Reconnecting...', { variant: 'warning' });
        setTimeout(() => this.listen(callback), 5000);
      }
    };

    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      this.snack.enqueueSnackbar('WebSocket error. Reconnecting...', { variant: 'error' });
      this.wsActive = false;
      this.ws = null;
      setTimeout(() => this.listen(callback), 5000);
    };
  }

  close() {
    if (this.ws) {
      this.ws.close(1000);
      this.wsActive = false;
      this.ws = null;
    }
  }
}

