    this.connectTS = Date.now();
    this.ws.binaryType = "arraybuffer";
    this.ws.onopen = this.onopen.bind(this);
    this.ws.onclose = this.onclose.bind(this);
    this.ws.onmessage = (e) => {
      if (e.data instanceof ArrayBuffer && this.ondata) {
        this.ondata(e.data);
      } else if (typeof e.data === 'string' && this.onmessage) {
        try {
          const msg = JSON.parse(e.data);
          if (this.onmessage[msg.c]) {
            this.onmessage[msg.c](msg);
          }
        } catch (err) {
          console.error("Failed to parse JSON message:", err, e.data);
        }
      }
    };
    this.ws.onerror = (e) => {
      console.error("WebSocket error:", e);
    };
    return true;
  }

  onopen() {
    this.wsState = WebSocket.OPEN;
    clearTimeout(this.reconnectTID);
    this.reconnectTID = 0;
  }

  onclose() {
    this.wsState = WebSocket.CLOSED;
    this.ws = null;
    if (this.reconnectTID === 0 && this.wsURL) {
      let timeout = this.RECONNECT_TIMEOUT;
      if (Date.now() - this.connectTS < this.RECONNECT_TIMEOUT) {
        timeout = this.RECONNECT_TIMEOUT - (Date.now() - this.connectTS);
      }
      this.reconnectTID = setTimeout(() => {
        this.onconnect();
      }, timeout);
      return true;
    }
    return false;
  }

  ondisconnect() {
    clearTimeout(this.reconnectTID);
    this.reconnectTID = 0;
    if (this.ws) {
      this.ws.close();
      this.ws = null;
      this.wsState = WebSocket.CLOSED;
    }
  }

  send(e) {
    if (this.ws && this.wsState === WebSocket.OPEN) {
      try {
        this.ws.send(JSON.stringify(e));
      } catch (err) {
        console.error("Failed to send JSON message:", err, e);
      }
    }
  }
