function startRecording(data) {
  let ws = new WebSocket(`wss://${data.host}:${data.port}`);
  let isServerReady = false;
  let isCapturing = false;
  let mediaRecorder;

  ws.onopen = () => {
    ws.send(JSON.stringify({
      action: 'start',
      language: data.language,
      task: data.task,
      modelSize: data.modelSize,
      useVad: data.useVad
    }));
  };

  ws.onmessage = async (event) => {
    const message = JSON.parse(event.data);

    switch (message.status) {
      case 'ready':
        isServerReady = true;
        browser.runtime.sendMessage({ command: 'serverReady' });
        break;
      case 'partial_result':
        browser.runtime.sendMessage({ command: 'partialResult', text: message.text });
        break;
      case 'final_result':
        browser.runtime.sendMessage({ command: 'finalResult', text: message.text });
        break;
      case 'error':
        console.error('Server error:', message.message);
        browser.runtime.sendMessage({ command: 'serverError', error: message.message });
        break;
    }
  };

  ws.onerror = (error) => {
    console.error('WebSocket error:', error);
    browser.runtime.sendMessage({ command: 'serverError', error: error.message });
  };

  ws.onclose = () => {
    isServerReady = false;
    isCapturing = false;
    browser.runtime.sendMessage({ command: 'serverDisconnected' });
  };

  navigator.mediaDevices.getUserMedia({ audio: true })
    .then(stream => {
      mediaRecorder = new MediaRecorder(stream);
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0 && isServerReady) {
          ws.send(event.data);
        }
      };

      browser.runtime.onMessage.addListener((message) => {
        if (message.command === 'startCapture' && !isCapturing) {
          isCapturing = true;
          mediaRecorder.start(100);
        } else if (message.command === 'stopCapture' && isCapturing) {
          isCapturing = false;
          mediaRecorder.stop();
          ws.send(JSON.stringify({ action: 'stop' }));
        }
      });
    })
    .catch(error => {
      console.error('Error accessing microphone:', error);
      browser.runtime.sendMessage({ command: 'micError', error: error.message });
    });
}

