class BingChatWebSocket {
  webSockets: Record<string, WebSocket | null> = {};

  async getSocketId(): Promise<string> {
    return new Promise((resolve, reject) => {
      const socket = new WebSocket('wss://sydney.bing.com/sydney/ChatHub');
      let socketId: string | null = null;

      socket.onopen = async () => {
        const handshake = { protocol: 'json', version: 1 };
        socket.send(JSON.stringify(handshake));
      };

      socket.onmessage = (event) => {
        if (!socketId) {
          const data = JSON.parse(event.data);
          if (data.type === 1) {
            socketId = data.invocationId;
            this.webSockets[socketId] = socket;
            resolve(socketId);
          } else {
            socket.close();
            reject(new Error('Invalid handshake response'));
          }
        }
      };

      socket.onerror = (err) => {
        reject(err);
      };

      socket.onclose = () => {
        if (socketId) {
          delete this.webSockets[socketId];
        }
      };
    });
  }


  async ping(socketId: string): Promise<void> {
    const socket = this.webSockets[socketId];
    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.send(JSON.stringify({ type: 6 }));
    } else {
      throw new Error('Socket not found or not open');
    }
  }

  async send(socketId: string, msg: object, onMessage: (data: any) => void): Promise<any> {
    const socket = this.webSockets[socketId];

    return new Promise((resolve, reject) => {
      if (socket && socket.readyState === WebSocket.OPEN) {
        socket.send(JSON.stringify(msg));

        const messageListener = (event: MessageEvent) => {
          const data = JSON.parse(event.data);
          onMessage(data);
          if (data.type === 2) {
            socket.removeEventListener('message', messageListener);
            resolve(data);
          }
        };

        socket.addEventListener('message', messageListener);

        socket.onerror = (err) => {
          reject(err);
        }
      } else {
        reject(new Error('Socket not found or not open'));
      }
    });
  }

  async close(socketId: string): Promise<void> {
    const socket = this.webSockets[socketId];
    if (socket) {
      socket.close();
      delete this.webSockets[socketId];
    }
  }
}

