    this.sendQueue = [];
    this.pendingReconnect = null;
    this.onOpenCallback = null;
    this.onCloseCallback = null;
    this.onMessageCallback = null;
  }

  fireSendQueue() {
    while (this.sendQueue.length > 0 && this.wsClient && this.wsClient.readyState === WebSocket.OPEN) {
      const { message, resolve } = this.sendQueue.shift();
      this.wsClient.send(message);
      resolve();
    }
  }

  clearSendQueue() {
    while (this.sendQueue.length > 0) {
      const { reject } = this.sendQueue.shift();
      reject(new Error('WebSocket connection closed'));
    }
  }

  planToReconnect(rpcUrl, options) {
    if (this.pendingReconnect) {
      clearTimeout(this.pendingReconnect);
    }
    this.pendingReconnect = setTimeout(() => {
      this.reconnect(rpcUrl, options);
      this.pendingReconnect = null;
    }, options.reconnectInterval || 5000);
  }

  init() {
    if (this.wsClient) {
      this.wsClient.onclose = null;
      this.wsClient.onopen = null;
      this.wsClient.onmessage = null;
      this.wsClient.close();
      this.wsClient = null;
    }
    this.clearSendQueue();
  }

  connect(rpcUrl, options, onOpenCallback, onCloseCallback, onMessageCallback) {
    this.init();
    this.onOpenCallback = onOpenCallback;
    this.onCloseCallback = onCloseCallback;
    this.onMessageCallback = onMessageCallback;

    this.wsClient = new WebSocket(rpcUrl, options.protocols);

    this.wsClient.onopen = () => {
      this.fireSendQueue();
      if (this.onOpenCallback) {
        this.onOpenCallback();
      }
    };

    this.wsClient.onclose = (event) => {
      if (this.onCloseCallback) {
        this.onCloseCallback(event);
      }
      this.planToReconnect(rpcUrl, options);
    };

    this.wsClient.onmessage = (event) => {
      if (this.onMessageCallback) {
        this.onMessageCallback(event);
      }
    };

    this.wsClient.onerror = (error) => {
      console.error("WebSocket error:", error);
    };
  }

  reconnect(rpcUrl, options) {
    this.connect(rpcUrl, options, this.onOpenCallback, this.onCloseCallback, this.onMessageCallback);
  }

  send(requestContext) {
    return new Promise((resolve, reject) => {
      this.sendQueue.push({ message: JSON.stringify(requestContext), resolve, reject });
      this.fireSendQueue();
    });
  }

  getReadyState() {
    return this.wsClient ? this.wsClient.readyState : null;
  }
}

