      try {
        this.wss = new WebSocket.Server(this.options);
        this.wss.on('listening', () => {
          resolve(this.wss);
        });
        this.wss.on('error', reject);
      } catch (error) {
        reject(error);
      }
    });
  }

  onConnection(callback) {
    if (!this.wss) {
      throw new Error('WebSocket server not initialized. Call createServer() first.');
    }
    this.wss.on('connection', (ws) => {
      const uuid = uuidv4();
      callback(ws, uuid);
    });
  }

  close() {
    if (this.wss) {
      this.wss.close();
      this.wss = null;
    }
  }
