const WebSocket = require('ws');
const { Session } = require('inspector');

function createWebSocketServer(port) {
  const wss = new WebSocket.Server({ port });

  wss.on('connection', ws => {
    const session = new Session();
    session.connect();

    ws.on('message', message => {
      try {
        const parsedMessage = JSON.parse(message);
        session.post(parsedMessage.method, parsedMessage.params, (err, result) => {
          if (err) {
            console.error('Inspector error:', err);
            ws.send(JSON.stringify({ id: parsedMessage.id, error: err.message }));
            return;
          }
          ws.send(JSON.stringify({ id: parsedMessage.id, result }));
        });
      } catch (err) {
        console.error('Invalid message:', err);
        ws.send(JSON.stringify({ error: 'Invalid message' }));
      }
    });

    ws.on('error', err => {
      console.error('WebSocket error:', err);
      session.disconnect();
    });

    ws.on('close', () => {
      session.disconnect();
    });

    session.on('inspectorNotification', message => {
      ws.send(JSON.stringify({ method: message.method, params: message.params }));
    });
  });

