const WebSocket = require('ws');

const DEFAULT_OPTIONS = {
  verbose: false,
  logger: console
};

class XVIZServer {
  constructor(handlers, options = {}, callback) {
    if (!handlers || handlers.length === 0) {
      throw new Error('Must provide at least one handler');
    }

    this.handlers = handlers;
    this.options = {...DEFAULT_OPTIONS, ...options};

    this._server = new WebSocket.Server(this.options, callback);

    this._server.on('connection', this.handleSession.bind(this));
  }

  get server() {
    return this._server;
  }

  close(cb) {
    this._server.close(cb);
  }

  async handleSession(socket, request) {
    let sessionHandled = false;

    for (const handler of this.handlers) {
      try {
        const handled = await handler.handleSession(socket, request);
        if (handled) {
          sessionHandled = true;
          break;
        }
      } catch (error) {
        this.log('Error handling session:', error);
        socket.close();
        return;
      }
    }

    if (!sessionHandled) {
      this.log('No handler accepted the session. Closing connection.');
      socket.close();
    }
  }


  log(...msg) {
    if (this.options.verbose && this.options.logger) {
      this.options.logger.log(...msg);
    }
  }
}

module.exports = XVIZServer;

