    const response = await axios.get(url, {
      headers: {
        Accept: 'application/json',
        withCredentials: true,
      },
    });

    const data = response.data;

    for (const icon of data) {
      try {
        const iconName = icon.name;
        const existingIcon = icons.find(i => i.name === iconName);

        if (existingIcon) {
          duplicates[iconName] = (duplicates[iconName] || 0) + 1;
          continue;
        }

        const svgResponse = await axios.get(icon.svg, { responseType: 'arraybuffer' });
        const svgPath = path.join(targetImagePath, `${iconName}.svg`);

        fs.writeFileSync(svgPath, svgResponse.data);

