const express = require('express');
const connect = require('connect');
const dataIn = require('./dataIn');
const locker = require('./locker');
const oembed = require('./oembed');
const async = require('async');
const logger = require('./logger');
const util = require('util');

function createServer() {
  const app = express();
  const server = require('http').createServer(app);

  app.use(connect.urlencoded());
  app.use(connect.json());
  app.use(express.static(__dirname + '/public'));

  app.get('/update', function(req, res) {
    dataIn.reindex(function(err) {
      if (err) {
        res.send(500);
      } else {
        res.send(200);
      }
    });
  });

  app.get('/embed', function(req, res) {
    oembed.embed(req.query.url, function(err, result) {
      if (err) {
        res.send(err.code, err.message);
      } else {
        res.json(result);
      }
    });
  });

  app.post('/events', function(req, res) {
    if (!req.body.events) {
      return res.send(400, 'Events payload missing');
    }
    async.forEachSeries(req.body.events, locker.queue, function(err) {
      if (err) {
        return res.send(500);
      }
      res.send(202);
    });
  });

  for (const k in util) {
    if (typeof util[k] === 'function') {
      app.get('/' + k, function(req, res) {
        const args = [];
        for (const k in req.query) {
          args.push(req.query[k]);
        }
        util[k].apply(null, args.concat(function(err, result) {
          if (err) {
            res.send(500, err.message);
          } else {
            res.json(result);
          }
        }));
      });
    }
  }

  let port = 0;
  server.listen(port, function() {
    port = server.address().port;
    process.stdout.write(JSON.stringify({port: port}));
  });
}

