const express = require('express');
const fs = require('fs');
const path = require('path');

class FOAAS {
  constructor(options) {
    this.app = express();
    this.operations = {};
    this.operationsArray = [];

    this.app.use(express.static(path.join(__dirname, 'public')));
    this.app.get('/', this.sendIndex.bind(this));

    const operationsFilePath = path.join(__dirname, 'operations.json');
    fs.readFile(operationsFilePath, 'utf8', (err, data) => {
      if (err) {
        console.error('Error loading operations:', err);
        return;
      }

      try {
        const operationsData = JSON.parse(data);
        this.operations = operationsData.operations;
        this.operationsArray = Object.keys(this.operations).map(key => ({
          name: key,
          url: `/${key}`,
          fields: this.operations[key].fields,
        }));

        for (const operationName in this.operations) {
          this.app.get(`/${operationName}`, (req, res) => {
            const operation = this.operations[operationName];
            let message = operation.message;
            for (const field of operation.fields) {
              const value = req.query[field.field];
              if (value) {
                message = message.replace(`:${field.field}`, value);
              }
            }
            this.output(req, res, message, operation.subtitle);
          });
        }
      } catch (parseError) {
        console.error('Error parsing operations JSON:', parseError);
      }
    });
  }

  sendIndex(req, res) {
    res.sendFile(path.join(__dirname, 'public', 'index.html'));
  }

  start(port) {
    if (!port) {
      console.error('Error: Port must be specified.');
      return;
    }
    this.server = this.app.listen(port, () => {
      console.log(`FOAAS server listening on port ${port}`);
    });
  }

  output(req, res, message, subtitle) {
    const response = {
      message: message,
      subtitle: subtitle,
    };
    res.json(response);
  }
}

module.exports = FOAAS;

