const express = require('express');
const exphbs = require('express-handlebars');
const path = require('path');

function getServer(P) {
  const port = P || process.env.PORT || 8000;
  const isReferenceMode = port === 8000;

  process.title = isReferenceMode ? 'reference-server' : 'development-server';

  const server = express();

  const viewsDir = isReferenceMode
    ? path.resolve(__dirname, '..', 'views')
    : path.resolve(__dirname, '..', '..', 'views');

  const hbs = exphbs.create({
    defaultLayout: 'main',
    layoutsDir: path.join(viewsDir, 'layouts'),
    partialsDir: path.join(viewsDir, 'partials'),
    helpers: {
      json(context) {
        return JSON.stringify(context);
      },
    },
  });

  server.engine('handlebars', hbs.engine);
  server.set('view engine', 'handlebars');
  server.set('views', viewsDir);

  server.use('/static', express.static(path.join(__dirname, '..', '..', 'dist', 'static')));

  server.get('/', (req, res) => {
    res.render('index', {
      layout: false,
    });
  });

  server.get('/test/:component', (req, res) => {
    const component = req.params.component;
    try {
      const config = require(`../../src/components/${component}/config.json`);
      res.render('component-test', {
        component,
        config,
      });
    } catch (error) {
      console.error(`Error loading config for component ${component}:`, error);
      res.status(500).send(`Error loading config for component ${component}`);
    }
  });


  server.listen(port, () => {
    console.log(`Server listening on port ${port}`);
  });

  return server;
}

module.exports = getServer;

